INSERT INTO help (parentID, hash, topic, content, created, updated) VALUES (-1, md5('help_editor'), 'Help Editor', 'Help Editor module displays existing help content for various modules in LORIS. A list of entries will appear that organizes help content by Topic, Parent Topic and Content. The selection filter allows users to search by Topic or Search keyword. They search keyword returns result if the search item appears in either the Topic or Content text. At the top of the table, column headings will appear underlined and show a click icon when the user hovers over the heading title. Data can be sorted in ascending order according to a given column by clicking on the column heading (i.e. Topic, Parent Topic etc.), and by clicking again, in descending order.\r\n\r\nEditing Content : The content for any module can be edited by clicking on the Topic or Parent topic of choice.', '2014-09-01 00:00:00', NULL);
INSERT INTO help (parentID, hash, topic, content, created, updated) SELECT help.helpID, md5('edit_help_content'), 'Edit Help Content', 'This page will display a title and existing content for each module. Both the title and the content are editable. The text can be updated directed on the page and to save the changes click on the Save button at the bottom.', '2014-09-01 00:00:00', NULL FROM help WHERE hash=md5('help_editor');
